#include <stdint.h>
#include "Stm32f401-gpio.h"

void delay(volatile uint32_t time) {
    for(uint32_t i = 0; i < time * 1000; i++);
}

int main(void)
{
	GPIO_EnableClock(GPIOA);

	GPIOA->MODER &= ~(3<<(2*2));
	GPIOA->MODER |=(1<<(2*2));
	GPIOA->ODR |=(1<<2);

	while(1)
	{
		if(!(GPIOA->IDR & (1 << 0))){
			GPIOA->ODR |= (1<<2); // turn LED ON
		}
		else{
			GPIOA->ODR &= ~(1<<2); // turn LED OFF
		}
	}
}
